mapper.insert:
	 INSERT INTO DOG_PARK( 	 
	 	 INUM			
	    ,IID      		
		,IPHOTO 			
		,IUPLOAD 			
		,ICONTENT 		
      	,DELETEYN   		 
      	,IUPDATE 		
      	,IINSERT 		
     ) VALUES ( 	 
   	     #{inum, jdbcType=VARCHAR} 				
		,#{iid, jdbcType=VARCHAR} 				
		,#{iphoto, jdbcType=VARCHAR} 				
       	,#{iupload, jdbcType=VARCHAR}  				
       	,#{icontent, jdbcType=VARCHAR}  			
       	,'Y'  									
       	,SYSDATE  								
       	,SYSDATE 								
      )

mapper.update:
UPDATE DOG_PARK
SET

	  ICONTENT     = #{icontent, jdbcType=VARCHAR}
	, IUPDATE = SYSDATE
WHERE DELETEYN='Y'
AND   INUM = #{inum, jdbcType=VARCHAR}
