mapper.select:
SELECT 
    COUNT(A.ANO) 
FROM AGENCY A 
WHERE TNO = #{tno, jdbcType=VARCHAR}
AND CNO = #{cno, jdbcType=VARCHAR}

SELECT 
    A.MATCHYN MATCHYN
FROM AGENCY A 
WHERE TNO = #{tno, jdbcType=VARCHAR}
AND CNO = #{cno, jdbcType=VARCHAR}

mapper.update:
UPDATE AGENCY SET
	MATCHYN = 'N',
	UPDATEDATE = SYSDATE
WHERE TNO = #{tno, jdbcType=VARCHAR}
AND CNO = #{cno, jdbcType=VARCHAR}

UPDATE AGENCY SET
	MATCHYN = 'Y',
	UPDATEDATE = SYSDATE
WHERE TNO = #{tno, jdbcType=VARCHAR}
AND CNO = #{cno, jdbcType=VARCHAR}
