mapper.insert:
INSERT INTO MEMBER( 	 
							 MNO      		
							,MID 			
							,MPW 			
							,MEMAIL 			
                          	,MNAME   		 
                          	,MBIRTH 		
                          	,MPHOTO 			
                          	,MZONECODE  	
                          	,MROADADDRESS 	
                          	,MJIBUNADDRESS 	
                          	,DELETEYN		
                          	,INSERTDATE	 	
                          	,UPDATEDATE		
                         ) 
                  VALUES
                  		( 	 
                  			 #{mno, jdbcType=VARCHAR} 				
                  			,#{mid, jdbcType=VARCHAR} 				
                          	,#{mpw, jdbcType=VARCHAR}  				
                          	,#{memail, jdbcType=VARCHAR}  			
                  			,#{mname, jdbcType=VARCHAR} 			 
                          	,#{mbirth, jdbcType=VARCHAR}  			
                  			,#{mphoto, jdbcType=VARCHAR} 				
                          	,#{mzonecode, jdbcType=VARCHAR} 		
                          	,#{mroadaddress, jdbcType=VARCHAR}  	
                  			,#{mjibunaddress, jdbcType=VARCHAR} 	
                          	,'Y'  									
                          	,SYSDATE  								
                          	,SYSDATE 								
                         )

mapper.update:
UPDATE MEMBER
SET
	  MNAME      = #{mname, jdbcType=VARCHAR}
	, MEMAIL     = #{memail, jdbcType=VARCHAR}
	, MBIRTH     = #{mbirth, jdbcType=VARCHAR}
	, MZONECODE  = #{mzonecode, jdbcType=VARCHAR}
	, MROADADDRESS = #{mroadaddress, jdbcType=VARCHAR}
	, MJIBUNADDRESS = #{mjibunaddress, jdbcType=VARCHAR}
	, MPHOTO     = #{mphoto, jdbcType=VARCHAR}
	, UPDATEDATE = SYSDATE
WHERE DELETEYN='Y'
AND   MNO = #{mno, jdbcType=VARCHAR}
