mapper.select:
SELECT * FROM ( SELECT A.CRNUM CRNUM ,A.MID MID ,A.RWRITER RWRITER ,A.CRSUBJECT CRSUBJECT ,A.CRSCORE CRSCORE ,A.CRMEMO CRMEMO ,A.CRREPORT CRREPORT ,TO_CHAR(A.INSERTDATE, 'YYYY-MM-DD') INSERTDATE ,TO_CHAR(A.UPDATEDATE, 'YYYY-MM-DD') UPDATEDATE
,CEIL(ROW_NUMBER() OVER(ORDER BY A.CRNUM)/ #{pageSize, jdbcType=VARCHAR} ) PAGENO ,COUNT(A.CRNUM) OVER() AS totalCount FROM REVIEW A WHERE A.DELYN = 'Y'
AND A.MID LIKE '%' || #{keyword, jdbcType=VARCHAR} || '%'
ORDER BY 1 DESC
) WHERE PAGENO = #{curPage, jdbcType=VARCHAR}
